import React, { Component } from 'react';
import { Form, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import "./Login.css";

const emailRegex = RegExp(
  /^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/
);

const formValid = ({ formErrors, ...rest }) => {
  let valid = true;

  // validate form errors being empty
  Object.values(formErrors).forEach(val => {
    val.length > 0 && (valid = false);
  });

  // validate the form was filled out
  Object.values(rest).forEach(val => {
    val === null && (valid = false);
  });
};


class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
      formErrors: {
        email: "",
        password: "",
      }
    }
  }

  // handleChangeEmail = (event) => {
  //   this.setState({ email: event.target.value });
  // }

  // handleChangePassword = (event) => {
  //   this.setState({ password: event.target.value });
  // }

  handleChange = (e) => {
    e.preventDefault();
    const {name, value} = e.target;
    let formErrors = {...this.state.formErrors};

    switch(name){
      case "email":
        formErrors.email = emailRegex.test(value)
          ? ""
          : "invalid email address";
        break;

        case "password":
          value.length < 6 ? "minimum 6 characaters required" : "";
        break;

        default:
        break;
    };

    this.setState({ formErrors, [name]: value }, () => console.log(this.state));
  }

  handleSubmit() {
    let authData = {
      email: this.state.email,
      password: this.state.password
    }

    console.log(authData);
  }

  render() {
    const { formErrors } = this.state;

    return (
      <div className="background">
        <div className="container">
          <div className="row">
            <div className="col-md-4"></div>
            <div className="col-md-5" style={{ height: "100vh" }}>

              <form
                className="form align-middle w-75"
                style={{
                  position: "relative",
                  margin: "50%",
                  textAlign: "center"
                }}
              >
                <h3>
                  <em>
                    People FreeLance
                </em>
                </h3>

                <div className="form-group">
                  <Form.Group controlId="formBasicEmail">
                    <Form.Label style={{ float: "left" }}><h6><em>Email address</em></h6></Form.Label>
                    <Form.Control
                      className={formErrors.email.length > 0 ? "error" : null}
                      type="email"
                      placeholder="Enter email"
                      onChange={this.handleChangeEmail}
                      required
                    />
                    {
                      formErrors.email.length > 0 && (
                        <span className="errorMessage">{formErrors.email}</span>
                      )}
                    }
                  </Form.Group>

                  <Form.Group controlId="formBasicPassword">
                    <Form.Label style={{ float: "left" }}><h6><em>Password</em></h6></Form.Label>
                    <Form.Control
                      type="password"
                      placeholder="Password"
                      onChange={this.handleChangePassword}
                      required
                    />
                  </Form.Group>
                </div>
                <Button color="primary" onSubmit={this.handleSubmit}>Login</Button>{' '}
                <p>Not registered? <Link to='/Register'>Create Account</Link></p>
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Login;
