import React from 'react';
import { Form, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import './Register.css';

class Register extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            email: "",
            password: "",
            confirmPassord: "",
            role: "",
            formErrors: {
                email: "",
                password: "",
                confirmPassword: "",
                role: ""
              }
        }
    }

    validateErrors = (name, value, formErrors) => {
        switch (name) {
          case "email":
            formErrors.email = emailRegex.test(value)
              ? ""
              : "Invalid email address ❌";
            break;
    
          case "password":
            formErrors.password =
              value.length < 6 ? "Minimum 6 characaters required ❌" : "";
            break;
    
          case "confirmPassword":
            formErrors.confirmPassword = 
              value.length < 6 ? "Minimum 6 characaters required ❌": "";
            break;
    
          case "role":
              formErrors.role = 
                value === "Choose role" ? "You need to choose a role for user": "";
          default:
            break;
        };
      }

      handleChange = (e) => {
        e.preventDefault();
        const { name, value } = e.target;
        let formErrors = { ...this.state.formErrors };
        this.validateErrors(name, value, formErrors);
        this.setState({ formErrors, [name]: value });
      }

    render() {
        const formErrors = this.state.formErrors;
        
        return (
            <div className="bg">
                <div className="container">
                    <div className="row">
                        <div className="col-md-2"></div>
                        <div className="col-md-5" style={{ height: "100vh" }}>

                            <form
                                className="form align-middle w-75"
                                style={{
                                    position: "relative",
                                    margin: "50%",
                                    textAlign: "center"
                                }}
                            >
                                <h3>
                                    <em>
                                        Sign Up to People FreeLance
                                    </em>
                                </h3>

                                <div className="form-group">
                                    <Form.Group controlId="formBasicEmail">
                                        <Form.Label style={{ float: "left" }}><h6><em>Email</em></h6></Form.Label>
                                        <Form.Control type="email" placeholder="Enter email" onChange = {this.handleChangeEmail}/>
                                    </Form.Group>

                                    <Form.Group controlId="formBasicPassword">
                                        <Form.Label style={{ float: "left" }}><h6><em>Password</em></h6></Form.Label>
                                        <Form.Control type="password" placeholder="Password" onChange = {this.handleChangePassword}/>
                                    </Form.Group>

                                    <Form.Group controlId="formBasicConfirmPassword">
                                        <Form.Label style={{ float: "left" }}><h6><em style={{ color: "white" }}>Confirm password</em></h6></Form.Label>
                                        <Form.Control type="password" placeholder="Confirm password" onChange = {this.handleChangeConfirmPassword}/>
                                    </Form.Group>

                                    <label style={{ float: "left" }}><h6><em style={{ color: "white" }} >What role you will have?</em></h6></label>
                                    <select className="form-control" onChange = {this.handleChaneRole}>
                                        <option defaultValue>Choose role</option>
                                        <option>Employee</option>
                                        <option>Employeer</option>
                                    </select>

                                </div>
                                <Button color="primary"><em>Sing Up</em></Button>{' '}
                                <hr />
                                <h6><em style={{ color: "white" }}>Back to <Link to='/Login'>Sing in</Link></em></h6>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
export default Register
