import React from "react";
import { Route, Redirect } from "react-router-dom";

export const PrivateRoute = ({ component: Component, ...rest }) => (
    <Route>
        render={props =>
            localStorage.getItem('login') ? (
                <Component {...props} />
            ) : (
                <Redirect
                    to = {{
                        pathname: "/Login",
                        state: {from: props.location}
                    }}
                />
            )
        }
    </Route>
);
