import React, { Component } from 'react';
import { Form, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import "./Login.css"; 

const emailRegex = RegExp(
  /^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/
);

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
      errors: {
        
      }
    }
  }

  handleChangeEmail = (event) => {
    this.setState({ email: event.target.value });
  }

  handleChangePassword = (event) => {
    this.setState({ password: event.target.value });
  }

  handleSubmit(){
    let authData = {
      email: this.state.email,
      password: this.state.password
    }

    console.log(authData);
  }

  render() {
    return (
      <div className="background">
        <div className="container">
          <div className="row">
            <div className="col-md-4"></div>
            <div className="col-md-5" style={{ height: "100vh" }}>

              <form
                className="form align-middle w-75"
                style={{
                  position: "relative",
                  margin: "50%",
                  textAlign: "center"
                }}
              >
                <h3>
                  <em>
                    People FreeLance
                </em>
                </h3>

                <div className="form-group">
                  <Form.Group controlId="formBasicEmail">
                    <Form.Label style={{ float: "left" }}><h6><em>Email address</em></h6></Form.Label>
                    <Form.Control 
                        type="email" 
                        placeholder="Enter email" 
                        onChange = {this.handleChangeEmail}
                        required
                      />
                  </Form.Group>

                  <Form.Group controlId="formBasicPassword">
                    <Form.Label style={{ float: "left" }}><h6><em>Password</em></h6></Form.Label>
                    <Form.Control 
                      type="password" 
                      placeholder="Password" 
                      onChange = {this.handleChangePassword}
                      required
                      />
                  </Form.Group>
                </div>
                <Button color="primary" onSubmit = {this.handleSubmit}>Login</Button>{' '}
                <p>Not registered? <Link to='/Register'>Create Account</Link></p>
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Login;
